import { httpAction } from '@/api/manage'; // import JCron from "@/components/jeecg/JCron";

import cronValidator from "@/components/jeecg/JEasyCron/validator";
export default {
  name: "QuartzJobModal",
  components: {// JCron,
  },
  data: function data() {
    return {
      title: "操作",
      buttonStyle: 'solid',
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      cron: {
        label: '',
        value: ''
      },
      confirmLoading: false,
      validatorRules: {
        cronExpression: [{
          required: true,
          message: '请输入cron表达式!'
        }, {
          validator: cronValidator
        }],
        jobClassName: [{
          required: true,
          message: '请输入任务类名!'
        }]
      },
      url: {
        add: "/sys/quartzJob/add",
        edit: "/sys/quartzJob/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      // 统一设置默认值
      this.edit({
        cronExpression: '* * * * * ? *',
        status: 0
      });
    },
    edit: function edit(record) {
      var _this = this;

      this.visible = true;
      this.$nextTick(function () {
        _this.$refs.form.resetFields();

        _this.model = Object.assign({}, record);
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (ok, err) {
        if (ok) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          console.log('提交参数', _this2.model);
          httpAction(httpurl, _this2.model, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
              that.close();
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
