import { queryDepartTreeList, searchByKeywords } from '@/api/api';
export default {
  name: 'AddressListLeft',
  props: ['value'],
  data: function data() {
    return {
      cardLoading: true,
      loading: false,
      treeDataSource: [],
      selectedKeys: [],
      expandedKeys: []
    };
  },
  created: function created() {
    this.queryTreeData();
  },
  methods: {
    queryTreeData: function queryTreeData(keyword) {
      this.commonRequestThen(queryDepartTreeList({
        departName: keyword ? keyword : undefined
      }));
    },
