import Vue from 'vue';
import { USER_INFO, ENHANCE_PRE } from "@/store/mutation-types";
var getters = {
  device: function device(state) {
    return state.app.device;
  },
  theme: function theme(state) {
    return state.app.theme;
  },
  color: function color(state) {
    return state.app.color;
  },
  token: function token(state) {
    return state.user.token;
  },
  avatar: function avatar(state) {
    state.user.avatar = Vue.ls.get(USER_INFO).avatar;
    return state.user.avatar;
  },
  username: function username(state) {
    return state.user.info.username;
  },
  nickname: function nickname(state) {
    state.user.info.realname = Vue.ls.get(USER_INFO).realname;
    return state.user.info.realname;
  },
  welcome: function welcome(state) {
    return state.user.welcome;
  },
  permissionList: function permissionList(state) {
    return state.user.permissionList;
  },
  userInfo: function userInfo(state) {
    state.user.info = Vue.ls.get(USER_INFO);
    return state.user.info;
  },
  addRouters: function addRouters(state) {
    return state.permission.addRouters;
  },
  onlAuthFields: function onlAuthFields(state) {
    return state.online.authFields;
  },
  enhanceJs: function enhanceJs(state) {
    return function (code) {
      state.enhance.enhanceJs[code] = Vue.ls.get(ENHANCE_PRE + code);
      return state.enhance.enhanceJs[code];
    };
  },
  sysSafeMode: function sysSafeMode(state) {
    return state.user.sysSafeMode;
  }
};
export default getters;
