import { httpAction } from '@/api/manage';
export default {
  name: "JeecgDemoModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      layout: {
        labelCol: {
          span: 3
        },
        wrapperCol: {
          span: 14
        }
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 3
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        name: [{
          required: true,
          message: '请输入姓名!'
        }, {
          min: 2,
          max: 30,
          message: '长度在 2 到 30 个字符',
          trigger: 'blur'
        }],
        email: [{
          required: false,
          type: 'email',
          message: '邮箱格式不正确',
          trigger: 'blur'
        }]
      },
      url: {
        add: "/test/jeecgDemo/add",
        edit: "/test/jeecgDemo/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      this.model = Object.assign({}, record);
      this.visible = true;
    },
    close: function close() {
      this.$refs.form.resetFields();
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this.model.id) {
            httpurl += _this.url.add;
            method = 'post';
          } else {
            httpurl += _this.url.edit;
            method = 'put';
          }

          httpAction(httpurl, _this.model, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
