import { httpAction } from '@/api/manage';
import moment from 'moment';
import JDate from '@/components/jeecg/JDate';
export default {
  components: {
    JDate: JDate
  },
  name: 'JeecgOrderTicketModal',
  data: function data() {
    return {
      title: '操作',
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      moment: moment,
      format: 'YYYY-MM-DD HH:mm:ss',
      disableSubmit: false,
      orderId: '',
      hiding: false,
      confirmLoading: false,
      validatorRules: {
        ticketCode: [{
          required: true,
          message: '请输入航班号!'
        }],
        tickectDate: [{
          required: true,
          message: '请输入航班时间!'
        }]
      },
      url: {
        add: '/test/order/addTicket',
        edit: '/test/order/editTicket'
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add(orderId) {
      if (orderId) {
        this.edit({
          orderId: orderId
        }, '');
      } else {
        this.$message.warning('请选择一条航班数据');
      }
    },
    detail: function detail(record) {
      this.edit(record, 'd');
    },
    edit: function edit(record, v) {
      if (v == 'e') {
        this.hiding = false;
        this.disableSubmit = false;
      } else if (v == 'd') {
        this.hiding = false;
        this.disableSubmit = true;
      } else {
        this.hiding = true;
        this.disableSubmit = false;
      }

      this.model = Object.assign({}, record);
      this.visible = true;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.$refs.form.resetFields();
    },
    handleOk: function handleOk() {
      var _this = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this.model.id) {
            httpurl += _this.url.add;
            method = 'post';
          } else {
            httpurl += _this.url.edit;
            method = 'put';
          }

          _this.model.mainId = _this.model.orderId;
          httpAction(httpurl, _this.model, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
