import Vue from 'vue';
import { ACCESS_TOKEN, ENCRYPTED_STRING } from '@/store/mutation-types';
import ThirdLogin from './third/ThirdLogin';
import LoginSelectTenant from './LoginSelectTenant';
import TwoStepCaptcha from '@/components/tools/TwoStepCaptcha';
import { getEncryptedString } from '@/utils/encryption/aesEncrypt';
import { timeFix } from '@/utils/util';
import LoginAccount from './LoginAccount';
import LoginPhone from './LoginPhone';
export default {
  components: {
    LoginSelectTenant: LoginSelectTenant,
    TwoStepCaptcha: TwoStepCaptcha,
    ThirdLogin: ThirdLogin,
    LoginAccount: LoginAccount,
    LoginPhone: LoginPhone
  },
  data: function data() {
    return {
      customActiveKey: 'tab1',
      rememberMe: true,
      loginBtn: false,
      requiredTwoStepCaptcha: false,
      stepCaptchaVisible: false,
      encryptedString: {
        key: "",
        iv: ""
      }
    };
  },
  created: function created() {
    Vue.ls.remove(ACCESS_TOKEN);
    this.getRouterData();
    this.rememberMe = true;
  },
  methods: {
    handleTabClick: function handleTabClick(key) {
      this.customActiveKey = key;
    },
    handleRememberMeChange: function handleRememberMeChange(e) {
      this.rememberMe = e.target.checked;
    },

    /**跳转到登录页面的参数-账号获取*/
    getRouterData: function getRouterData() {
      var _this = this;

      this.$nextTick(function () {
        var temp = _this.$route.params.username || _this.$route.query.username || '';

        if (temp) {
          _this.$refs.alogin.acceptUsername(temp);
        }
      });
    },
    //登录
    handleSubmit: function handleSubmit() {
      this.loginBtn = true;

      if (this.customActiveKey === 'tab1') {
        // 使用账户密码登录
        this.$refs.alogin.handleLogin(this.rememberMe);
      } else {
        //手机号码登录
        this.$refs.plogin.handleLogin(this.rememberMe);
      }
    },
    // 校验失败
    validateFail: function validateFail() {
      this.loginBtn = false;
    },
    // 登录后台成功
    requestSuccess: function requestSuccess(loginResult) {
      this.$refs.loginSelect.show(loginResult);
    },
    //登录后台失败
    requestFailed: function requestFailed(err) {
      var description = ((err.response || {}).data || {}).message || err.message || "请求出现错误，请稍后再试";
      this.$notification['error']({
        message: '登录失败',
        description: description,
        duration: 4
      }); //账户密码登录错误后更新验证码

      if (this.customActiveKey === 'tab1' && description.indexOf('密码错误') > 0) {
        this.$refs.alogin.handleChangeCheckCode();
      }

      this.loginBtn = false;
    },
    loginSelectOk: function loginSelectOk() {
      this.loginSuccess();
    },
    //登录成功
    loginSuccess: function loginSuccess() {
      this.$router.push({
        path: "/dashboard/analysis"
      }).catch(function () {
        console.log('登录跳转首页出错,这个错误从哪里来的');
      });
      this.$notification.success({
        message: '欢迎',
        description: "".concat(timeFix(), "\uFF0C\u6B22\u8FCE\u56DE\u6765")
      });
    },
    stepCaptchaSuccess: function stepCaptchaSuccess() {
      this.loginSuccess();
    },
